/* eslint-disable func-names */
import $ from 'jquery';

$(document).ready(() => {
  $('[data-bs-hover-animate]')
    .mouseenter(function () {
      const a = $(this);
      a.addClass(`animated ${a.attr('data-bs-hover-animate')}`);
    })
    .mouseleave(function () {
      const a = $(this);
      a.removeClass(`animated ${a.attr('data-bs-hover-animate')}`);
    });
});
